def semak_genap_ganjil(n):
  if n % 2 == 0:
    return 'Genap'
  else:
    return 'Ganjil'

def digit_total(n):
  jumlah = 0
  for d in str(n):
    jumlah += int(d)
  return jumlah

def cari_maks(senarai):
  terbesar = senarai[0]
  for n in senarai:
    if n > terbesar:
      terbesar = n
  return terbesar

def cari_min(senarai):
  kecil = senarai[0]
  for n in senarai:
    if n < kecil:
      kecil = n
  return kecil

def wujud(x, senarai):
  for elemen in senarai:
    if elemen == x:
      return True
  return False

def darab_ulang(a, b):
  hasil = 1
  for _ in range(b):
    hasil *= a
  return hasil
