const player1 = { score: 0 };
const player2 = { score: 0 };

// Gameplay
document.addEventListener('click', (event) => {
  if (event.target.id === 'player1') {
    player1.score++;
  } 
  else if (event.target.id === 'player2') {
    player2.score++;
  }
  updateScores();
});

// Display
function updateScores() {
  document.getElementById('player1Score').innerHTML = player1.score;
  document.getElementById('player2Score').innerHTML = player2.score;
}

def sortAscending(arr): 
  for i in range(len(arr)):
    for j in range(i+1, len(arr)):
      if arr[i] > arr[j]:
        temp = arr[i]
        arr[i] = arr[j]
        arr[j] = temp
  return arr

result = sortAscending([5, 10, 2, 20])
