"""Wrapper object for the file system / source tree."""

import codecs
import errno
import exceptions
import filecmp
import glob
import hashlib
import os
import shutil
import sys
import tempfile
import time


class FileSystem(object):
    """FileSystem interface for webkitpy.

    Unless otherwise noted, all paths are allowed to be either absolute
    or relative."""
    sep = os.sep
    pardir = os.pardir

    def abspath(self, path):
        # FIXME: This gross hack is needed while we transition from Cygwin to native Windows, because we
        # have some mixing of file conventions from different tools:
        if sys.platform == 'cygwin':
            path = os.path.normpath(path)
            path_components = path.split(os.sep)
            if path_components and len(path_components[0]) == 2 and path_components[0][1] == ':':
                path_components[0] = path_components[0][0]
                path = os.path.join('/', 'cygdrive', *path_components)

        return os.path.abspath(path)

    def realpath(self, path):
        return os.path.realpath(path)

    def path_to_module(self, module_name):
        """A wrapper for all calls to __file__ to allow easy unit testing."""
        # FIXME: This is the only use of sys in this file. It's possible this function should move elsewhere.
        return sys.modules[module_name].__file__  # __file__ is always an absolute path.

    def expanduser(self, path):
        return os.path.expanduser(path)

    def basename(self, path):
        return os.path.basename(path)

    def chdir(self, path):
        return os.chdir(path)

    def copyfile(self, source, destination):
        shutil.copyfile(source, destination)

    def dirname(self, path):
        return os.path.dirname(path)

    def exists(self, path):
        return os.path.exists(path)

    def dirs_under(self, path, dir_filter=None):
        """Return the list of all directories under the given path in topdown order.

        Args:
            dir_filter: if not None, the filter will be invoked
                with the filesystem object and the path of each dirfound.
                The dir is included in the result if the callback returns True.
        """
        def filter_all(fs, dirpath):
            return True
        dir_filter = dir_filter or filter_all

        dirs = []
        for (dirpath, dirnames, filenames) in os.walk(path):
            if dir_filter(self, dirpath):
                dirs.append(dirpath)
        return dirs

    def files_under(self, path, dirs_to_skip=[], file_filter=None):
        """Return the list of all files under the given path in topdown order.

        Args:
            dirs_to_skip: a list of directories to skip over during the
                traversal (e.g., .svn, resources, etc.)
            file_filter: if not None, the filter will be invoked
                with the filesystem object and the dirname and basename of
                each file found. The file is included in the result if the
                callback returns True.
        """
        def filter_all(fs, dirpath, basename):
            return True

        file_filter = file_filter or filter_all
        files = []
        if self.isfile(path):
            if file_filter(self, self.dirname(path), self.basename(path)):
                files.append(path)
            return files

        if self.basename(path) in dirs_to_skip:
            return []

        for (dirpath, dirnames, filenames) in os.walk(path):
            for d in dirs_to_skip:
                if d in dirnames:
                    dirnames.remove(d)

            for filename in filenames:
                if file_filter(self, dirpath, filename):
                    files.append(self.join(dirpath, filename))
        return files

    def getcwd(self):
        return os.getcwd()

    def glob(self, path):
        return glob.glob(path)

    def isabs(self, path):
        return os.path.isabs(path)

    def isfile(self, path):
        return os.path.isfile(path)

    def getsize(self, path):
        return os.path.getsize(path)

    def isdir(self, path):
        return os.path.isdir(path)

    def join(self, *comps):
        return os.path.join(*comps)

    def listdir(self, path):
        return os.listdir(path)

    def mkdtemp(self, **kwargs):
        """Create and return a uniquely named directory.

        This is like tempfile.mkdtemp, but if used in a with statement
        the directory will self-delete at the end of the block (if the
        directory is empty; non-empty directories raise errors). The
        directory can be safely deleted inside the block as well, if so
        desired.

        Note that the object returned is not a string and does not support all of the string
        methods. If you need a string, coerce the object to a string and go from there.
        """
        class TemporaryDirectory(object):
            def __init__(self, **kwargs):
                self._kwargs = kwargs
                self._directory_path = tempfile.mkdtemp(**self._kwargs)

            def __str__(self):
                return self._directory_path

            def __enter__(self):
                return self._directory_path

            def __exit__(self, type, value, traceback):
                # Only self-delete if necessary.

                # FIXME: Should we delete non-empty directories?
                if os.path.exists(self._directory_path):
                    os.rmdir(self._directory_path)

        return TemporaryDirectory(**kwargs)

    def maybe_make_directory(self, *path):
        """Create the specified directory if it doesn't already exist."""
        try:
            os.makedirs(self.join(*path))
        except OSError, e:
            if e.errno != errno.EEXIST:
                raise

    def move(self, source, destination):
        shutil.move(source, destination)

    def mtime(self, path):
        return os.stat(path).st_mtime

    def normpath(self, path):
        return os.path.normpath(path)

    def open_binary_tempfile(self, suffix):
        """Create, open, and return a binary temp file. Returns a tuple of the file and the name."""
        temp_fd, temp_name = tempfile.mkstemp(suffix)
        f = os.fdopen(temp_fd, 'wb')
        return f, temp_name

    def open_binary_file_for_reading(self, path):
        return codecs.open(path, 'rb')

    def read_binary_file(self, path):
        """Return the contents of the file at the given path as a byte string."""
        with file(path, 'rb') as f:
            return f.read()

    def write_binary_file(self, path, contents):
        with file(path, 'wb') as f:
            f.write(contents)

    def open_text_file_for_reading(self, path, errors='strict'):
        # Note: There appears to be an issue with the returned file objects
        # not being seekable. See http://stackoverflow.com/questions/1510188/can-seek-and-tell-work-with-utf-8-encoded-documents-in-python .
        return codecs.open(path, 'r', 'utf8', errors)

    def open_text_file_for_writing(self, path):
        return codecs.open(path, 'w', 'utf8')

    def open_stdin(self):
        return codecs.StreamReaderWriter(sys.stdin,
                                             codecs.getreader('utf8'),
                                             codecs.getwriter('utf8'),
                                             'replace')

    def read_text_file(self, path):
        """Return the contents of the file at the given path as a Unicode string.

        The file is read assuming it is a UTF-8 encoded file with no BOM."""
        with codecs.open(path, 'r', 'utf8') as f:
            return f.read()

    def write_text_file(self, path, contents):
        """Write the contents to the file at the given location.

        The file is written encoded as UTF-8 with no BOM."""
        with codecs.open(path, 'w', 'utf-8') as f:
            f.write(contents.decode('utf-8') if type(contents) == str else contents)

    def sha1(self, path):
        contents = self.read_binary_file(path)
        return hashlib.sha1(contents).hexdigest()

    def relpath(self, path, start='.'):
        return os.path.relpath(path, start)

    class _WindowsError(exceptions.OSError):
        """Fake exception for Linux and Mac."""
        pass

    def remove(self, path, osremove=os.remove):
        """On Windows, if a process was recently killed and it held on to a
        file, the OS will hold on to the file for a short while.  This makes
        attempts to delete the file fail.  To work around that, this method
        will retry for a few seconds until Windows is done with the file."""
        try:
            exceptions.WindowsError
        except AttributeError:
            exceptions.WindowsError = FileSystem._WindowsError

        retry_timeout_sec = 3.0
        sleep_interval = 0.1
        while True:
            try:
                osremove(path)
                return True
            except exceptions.WindowsError, e:
                time.sleep(sleep_interval)
                retry_timeout_sec -= sleep_interval
                if retry_timeout_sec < 0:
                    raise e

    def rmtree(self, path):
        """Delete the directory rooted at path, whether empty or not."""
        shutil.rmtree(path, ignore_errors=True)

    def copytree(self, source, destination):
        shutil.copytree(source, destination)

    def split(self, path):
        """Return (dirname, basename + '.' + ext)"""
        return os.path.split(path)

    def splitext(self, path):
        """Return (dirname + os.sep + basename, '.' + ext)"""
        return os.path.splitext(path)

    def compare(self, path1, path2):
        return filecmp.cmp(path1, path2)

"""
Forms to support third-party to first-party OAuth 2.0 access token exchange
"""
import provider.constants
from django.contrib.auth.models import User
from django.forms import CharField
from edx_oauth2_provider.constants import SCOPE_NAMES
from oauth2_provider.models import Application
from provider.forms import OAuthForm, OAuthValidationError
from provider.oauth2.forms import ScopeChoiceField, ScopeMixin
from provider.oauth2.models import Client
from requests import HTTPError
from social.backends import oauth as social_oauth
from social.exceptions import AuthException

from third_party_auth import pipeline


class AccessTokenExchangeForm(ScopeMixin, OAuthForm):
    """Form for access token exchange endpoint"""
    access_token = CharField(required=False)
    scope = ScopeChoiceField(choices=SCOPE_NAMES, required=False)
    client_id = CharField(required=False)

    def __init__(self, request, oauth2_adapter, *args, **kwargs):
        super(AccessTokenExchangeForm, self).__init__(*args, **kwargs)
        self.request = request
        self.oauth2_adapter = oauth2_adapter

    def _require_oauth_field(self, field_name):
        """
        Raise an appropriate OAuthValidationError error if the field is missing
        """
        field_val = self.cleaned_data.get(field_name)
        if not field_val:
            raise OAuthValidationError(
                {
                    "error": "invalid_request",
                    "error_description": "{} is required".format(field_name),
                }
            )
        return field_val

    def clean_access_token(self):
        """
        Validates and returns the "access_token" field.
        """
        return self._require_oauth_field("access_token")

    def clean_client_id(self):
        """
        Validates and returns the "client_id" field.
        """
        return self._require_oauth_field("client_id")

    def clean(self):
        if self._errors:
            return {}

        backend = self.request.backend
        if not isinstance(backend, social_oauth.BaseOAuth2):
            raise OAuthValidationError(
                {
                    "error": "invalid_request",
                    "error_description": "{} is not a supported provider".format(backend.name),
                }
            )

        self.request.session[pipeline.AUTH_ENTRY_KEY] = pipeline.AUTH_ENTRY_LOGIN_API

        client_id = self.cleaned_data["client_id"]
        try:
            client = self.oauth2_adapter.get_client(client_id=client_id)
        except (Client.DoesNotExist, Application.DoesNotExist):
            raise OAuthValidationError(
                {
                    "error": "invalid_client",
                    "error_description": "{} is not a valid client_id".format(client_id),
                }
            )
        if client.client_type not in [provider.constants.PUBLIC, Application.CLIENT_PUBLIC]:
            raise OAuthValidationError(
                {
                    # invalid_client isn't really the right code, but this mirrors
                    # https://github.com/edx/django-oauth2-provider/blob/edx/provider/oauth2/forms.py#L331
                    "error": "invalid_client",
                    "error_description": "{} is not a public client".format(client_id),
                }
            )
        self.cleaned_data["client"] = client

        user = None
        try:
            user = backend.do_auth(self.cleaned_data.get("access_token"), allow_inactive_user=True)
        except (HTTPError, AuthException):
            pass
        if user and isinstance(user, User):
            self.cleaned_data["user"] = user
        else:
            # Ensure user does not re-enter the pipeline
            self.request.social_strategy.clean_partial_pipeline()
            raise OAuthValidationError(
                {
                    "error": "invalid_grant",
                    "error_description": "access_token is not valid",
                }
            )

        return self.cleaned_data

import paramiko
import math
import json


class GetHPUXData:
    def __init__(self, ip, ssh_port, timeout, usr, pwd, use_key_file, key_file,
                 get_serial_info, get_hardware_info, get_os_details,
                 get_cpu_info, get_memory_info, ignore_domain, upload_ipv6, debug):
        self.machine_name = ip
        self.port = int(ssh_port)
        self.timeout = timeout
        self.username = usr
        self.password = pwd
        self.ssh = paramiko.SSHClient()
        self.use_key_file = use_key_file
        self.key_file = key_file
        self.get_serial_info = get_serial_info
        self.get_hardware_info = get_hardware_info
        self.get_os_details = get_os_details
        self.get_cpu_info = get_cpu_info
        self.get_memory_info = get_memory_info
        self.ignore_domain = ignore_domain
        self.upload_ipv6 = upload_ipv6
        self.debug = debug
        self.ssh = paramiko.SSHClient()
        self.conn = None
        self.root = False
        self.sysdata = {}
        self.nic_data = {'nic_parts': {}}
        self.ip_data = []
        self.disk_data = {'hdd_parts':[]}
        self.name = None
        self.paths = {}
        self.alldata = []
        self.name = None

        self.ssh.set_missing_host_key_policy(paramiko.AutoAddPolicy())

    def main(self):

        self.connect()
        self.are_u_root()
        self.get_sys_1()
        self.get_sys_2()
        self.get_macs()
        self.get_ips()


        self.get_cpu_num()
        self.get_disks()
        self.format_data()
        return self.alldata

    def connect(self):
        try:
            if not self.use_key_file:
                self.ssh.connect(str(self.machine_name), port=self.port,
                                 username=self.username, password=self.password, timeout=self.timeout)
            else:
                self.ssh.connect(str(self.machine_name), port=self.port,
                                 username=self.username, key_filename=self.key_file, timeout=self.timeout)
        except paramiko.AuthenticationException:
            print str(self.machine_name) + ': authentication failed'
            return None
        except Exception as err:
            print str(self.machine_name) + ': ' + str(err)
            return None

    def execute(self, cmd, need_sudo=False):
        if need_sudo and not self.root: # not working currently, maybe in the future
            cmd_sudo = "sudo -S -p '' %s" % cmd
            stdin, stdout, stderr = self.ssh.exec_command(cmd_sudo, timeout=30)
            stdin.write('%s\n' % self.password)
            stdin.flush()
        else:
            stdin, stdout, stderr = self.ssh.exec_command(cmd, timeout=30)

        data_err = stderr.readlines()
        data_out = stdout.readlines()
        return data_out, data_err

    def are_u_root(self):
        cmd = 'id -u'
        data, err = self.execute(cmd)
        if data[0].strip() == '0':
            self.root = True
        else:
            self.root = False
        if not self.root:
            print '[!] You must be root to run HP-UX discovery!'
            return

    def format_data(self):
        self.alldata.append(self.sysdata)
        self.alldata.append(self.nic_data)
        self.alldata.append(self.disk_data)


    def get_sys_1(self):
        cmd = '/usr/contrib/bin/machinfo'
        data_out, data_err = self.execute(cmd, False)
        if not data_err:
            raw = [x.strip().lower() for x in data_out if x not in ('', '\n', None)]
            for rec  in raw:
                if rec.startswith('memory:'):
                    ram = int(math.ceil(float(rec.split()[1])))
                    self.sysdata.update({'memory':ram})
                if rec.startswith('model:'):
                    model = rec.split(':')[1].strip().strip('"')
                    self.sysdata.update({'hardware': model})
                if rec.startswith('machine id number:'):
                    uuid = rec.split(':')[1].strip()
                    self.sysdata.update({'uuid': uuid})
                if rec.startswith('machine serial number'):
                    serial = rec.split(':')[1].strip()
                    self.sysdata.update({'serial_no': serial})
                if rec.startswith('nodename:'):
                    name = rec.split(':')[1].strip()
                    self.sysdata.update({'name': name})
                    self.name = name
                if rec.startswith('release:'):
                    os_version = rec.split(':')[1].strip()
                    osver = ' '.join(os_version.split()[1:]).strip()
                    self.sysdata.update({'os': 'hp-ux'})
                    self.sysdata.update({'osver': osver if osver else 'D42_NULL'})
        else:
            print '[!] Error in get_sys_1(). Message was: %s' % data_err

    def get_sys_2(self):
        cmd = '/opt/ignite/bin/print_manifest'
        data_out, data_err = self.execute(cmd, False)
        if not data_err:
            raw = [x.strip().lower() for x in data_out if x not in ('', '\n', None)]
            for rec in raw:

                if rec.startswith('model:'):
                    model = rec.split(':')[1].strip()
                    self.sysdata.update({'hardware': model})
                if rec.startswith('main memory:'):
                    m = rec.split(':')[1].split()[0]
                    ram = int(math.ceil(float(m.strip())))
                    self.sysdata.update({'memory': ram})
                if 'speed:' in rec and 'mhz' in rec:
                    cpu_speed= rec.split(':')[1].strip('mhz').strip()
                    self.sysdata.update({'cpupower': cpu_speed})
                if rec.startswith('hostname'):
                    name = rec.split(':')[1].strip()
                    self.name = name
                    self.sysdata.update({'name': name})
        else:
            print '[!] Error in get_sys_2(). Message was: %s' % data_err


    def get_macs(self):
        cmd = 'lanscan'
        data_out, data_err = self.execute(cmd, False)
        if not data_err:
            raw = [x.strip().lower() for x in data_out if x not in ('', '\n', None)]
            for rec  in raw:
                if rec.split()[3] == 'up':
                    words = rec.split()
                    nic_mac = words[1]
                    nic_name = words[4]
                    mac = ''.join(nic_mac.split('0x')[1:])
                    n=2
                    raw = [mac[i:i + n] for i in range(0, len(mac), n)]
                    macaddress = ':'.join(raw)
                    self.nic_data['nic_parts'].update({nic_name:{'serial_no':macaddress}})
        else:
            print '[!] Error in get_macs(). Message was: %s' % data_err

    def get_ips(self):
        ip_data = {}
        mac_data = {}

        for nic in self.nic_data['nic_parts']:
            mac = self.nic_data['nic_parts'][nic]['serial_no']
            ip_data.update({'device':self.name})
            ip_data.update({'tag': nic})
            mac_data.update({'device': self.name})
            mac_data.update({'port_name': nic})
            mac_data.update({'macaddress': mac})

            ip_data.update({'macaddress': mac})
            cmd = 'ifconfig %s | grep inet' % nic
            data_out, data_err = self.execute(cmd, False)
            if not data_err:
                raw = [x.strip().lower() for x in data_out if x not in ('', '\n', None)]
                for rec in raw:
                    ip = rec.split()[1].strip()
                    self.nic_data['nic_parts'][nic].update({'ipaddress':ip})
                    ip_data.update({'ipaddress': ip})
            else:
                print '[!] Error in get_ips(). Message was: %s' % data_err
            self.alldata.append(ip_data)
            self.alldata.append(mac_data)

    def get_cpu_num(self):
        cmd = 'ioscan -fnk|grep proc | wc -l'
        data_out, data_err = self.execute(cmd, False)

        if not data_err:
            raw = [x.strip().lower() for x in data_out if x not in ('', '\n', None)]
            if raw:
                cpu_num = raw[0]
                self.sysdata.update({'cpucount': cpu_num})
        else:
            print '[!] Error in get_cpu_num(). Message was: %s' % data_err

    def get_disks(self):
        cmd = 'ls /dev/rdisk/'
        data_out, data_err = self.execute(cmd, False)

        if not data_err:
            disks = list(set([x.strip().split('_')[0] for x in data_out if x]))
            for disk in disks:
                cmd = 'diskinfo /dev/rdisk/%s' % disk
                data_out, data_err = self.execute(cmd, False)
                if not data_err:
                    raw = [x.strip().lower() for x in data_out if x not in ('', '\n', None)]
                    disk = {}
                    for rec in raw:
                        if 'describe of ' in rec:  # another disk
                            if not len(disk) == 0:
                                self.disk_data['hdd_parts'].append(disk)
                                disk = {}
                        else:
                            if rec.startswith('product id'):
                                product = rec.split(':')[1].strip()
                                disk.update({'product': product})
                            if rec.startswith('size'):
                                size = int(math.ceil(float(rec.split(':')[1].split()[0].strip()) / 1024 / 1024))
                                disk.update({'hdd_size': size})
                                disk.update({'assignment': 'device'})
                                if self.name:
                                    disk.update({'device': self.name})
                    self.disk_data['hdd_parts'].append(disk)

        else:
            print '[!] Error in get_disks(). Message was: %s' % data_err

from typing import Optional, Sequence

from ...filth import Filth
from ..base import PostProcessor


class PrefixSuffixReplacer(PostProcessor):
    name = 'prefix_suffix_replacer'  # type: str

    def __init__(self, prefix: Optional[str] = '{{', suffix: Optional[str] = '}}', name: Optional[str] = None):
        super(PrefixSuffixReplacer, self).__init__(name=name)

        self.prefix = prefix
        self.suffix = suffix

    def process_filth(self, filth_list: Sequence[Filth]) -> Sequence[Filth]:
        for filth_item in filth_list:
            if filth_item.replacement_string is None:
                filth_item.replacement_string = filth_item.type.upper()

            if self.prefix is not None and self.suffix is not None:
                filth_item.replacement_string = self.prefix + filth_item.replacement_string + self.suffix
            elif self.prefix is not None:
                filth_item.replacement_string = self.prefix + filth_item.replacement_string
            elif self.suffix is not None:
                filth_item.replacement_string = filth_item.replacement_string + self.suffix

        return filth_list


__all__ = ['PrefixSuffixReplacer']

import tfi.json
import tensorflow as tf
import os.path

import tfi.data
import tfi.doc

from google.protobuf.json_format import ParseDict
from tfi.parse.docstring import GoogleDocstring

def _detect_method_documentation(*, bibliographer, model, method_name, method, signature_def):
    # NOTE(adamb) Since we don't want to be parsing rst here, we'll just rewrite
    #     it to include detected citations. Expect that this rst will be parsed
    #     for real when rendering HTML.
    docstr = GoogleDocstring(obj=method).result()
    docstr_sections = docstr['sections']
    text_sections = [v for k, v in docstr_sections if k == 'text']
    overview = "\n".join([l for t in text_sections for l in t])

    docstr['args'] = _enrich_docs_with_tensor_info(docstr['args'], signature_def.inputs)
    docstr['returns'] = _enrich_docs_with_tensor_info(docstr['returns'], signature_def.outputs)

    return tfi.doc.MethodDocumentation(
        name=method_name,
        overview=bibliographer.rewrite(overview),
        inputs=docstr['args'],
        outputs=docstr['returns'],
        examples=[
            tfi.doc.MethodDataDocumentation.generate(
                method=getattr(model, method_name),
                inputs={
                    input_name: eval("\n".join(input_val_lines), {}, {'m': model, 'tfi': tfi})
                    for input_name, _, input_val_lines in docstr['example args']
                },
            ),
        ],
    )

def detect_model_documentation(model):
        source = tfi.doc.ModelSource.detect(model)

        bibliographer = tfi.doc.Bibliographer()

        def maybeattr(o, attr, default=None):
            return getattr(o, attr) if o and hasattr(o, attr) else default

        # NOTE(adamb) Since we don't want to be parsing rst here, we'll just rewrite
        #     it to include detected citations. Expect that this rst will be parsed
        #     for real when rendering HTML.
        model_docstr = GoogleDocstring(obj=model).result()
        model_docstr_sections = model_docstr['sections']
        text_sections = [v for k, v in model_docstr_sections if k == 'text']
        overview = "\n".join([l for t in text_sections for l in t])

        return tfi.doc.ModelDocumentation(
            name=maybeattr(model, '__name__', type(model).__name__),
            hyperparameters=maybeattr(model, '__tfi_hyperparameters__', []),
            overview=bibliographer.rewrite(overview),
            implementation_notes=[],
            authors=[
                *[
                    {
                        "name": author['name'],
                        "url": author['url'],
                        "role_noun": "Commits",
                        "role_url": author['commits_url'],
                    }
                    for author in maybeattr(source, 'authors', [])
                ],
            ],
            source=source,
            facets_overview_proto=maybeattr(model, '__tfi_facets_overview_proto__'),
            methods=[
                _detect_method_documentation(
                    model=model,
                    bibliographer=bibliographer,
                    method_name=method_name,
                    method=getattr(model, method_name),
                    signature_def=signature_def,
                )
                for method_name, signature_def in maybeattr(model, '__tfi_signature_defs__').items()
            ],
            references=bibliographer.references(),
        )

def _tensor_info_str(tensor):
    if tensor.shape.ndims is None:
        return '%s ?' % tensor.dtype.name

    return '%s <%s>' % (
        tensor.dtype.name,
        ', '.join(['?' if n is None else str(n) for n in tensor.shape.as_list()]),
    )

def _enrich_docs_with_tensor_info(doc_fields, tensor_dict):
    existing = {k: v for k, _, v in doc_fields}
    return [
        (name, _tensor_info_str(tensor), existing.get(name, ''))
        for name, tensor in tensor_dict.items()
    ]

class MethodDocumentationLayout(object):
    def __init__(self, base_path, assets_extra_path):
        self.assets_extra_path = assets_extra_path
        self.metadata_path = os.path.join(base_path, 'metadata.json')
        self._base_path = base_path

    def file(self, subpath):
        return os.path.join(self._base_path, subpath)

class ModelDocumentationLayout(object):
    def __init__(self, model_dir):
        self.basename = os.path.basename(model_dir)
        self.assets_extra_path = os.path.join(model_dir, 'assets.extra')
        self.doc_path = os.path.join(self.assets_extra_path, 'doc')
        self.metadata_path = os.path.join(self.doc_path, 'metadata.json')
        self.methods_path = os.path.join(self.doc_path, 'methods')

    def method(self, method_name):
        return MethodDocumentationLayout(
            os.path.join(self.methods_path, method_name),
            self.assets_extra_path,
        )

def _read_json_else(path, default):
    if not os.path.exists(path):
      return default

    with open(path) as f:
      return tfi.json.load(f)

def _write_json(path, obj):
    os.makedirs(os.path.dirname(path), exist_ok=True)
    with open(path, 'w') as f:
        tfi.json.dump(obj, f)

class MethodDataDocumentationCodec(object):
    def __init__(self, method_layout):
        self._layout = method_layout

    def write(self, method_example):
        _write_json(
            self._layout.file('inputs.json'),
            {
                name: value
                for name, value in method_example.inputs().items()
            }
        )

        if method_example.outputs() is not None:
            _write_json(
                self._layout.file('outputs.json'),
                {
                    name: value
                    for name, value in method_example.outputs().items()
                }
            )

    def read(self, signature_def):
        return tfi.doc.MethodDataDocumentation(
            inputs=self._detect(
                lambda: self._read_json_tf_example_from(
                    signature_def.inputs,
                    'inputs.pb.json',
                ),
                lambda: self._read_json_example_from(
                    signature_def.inputs,
                    'inputs.json',
                ),
            ),
            outputs=self._detect(
                lambda: self._read_json_tf_example_from(
                    signature_def.outputs,
                    'outputs.pb.json',
                ),
                lambda: self._read_json_example_from(
                    signature_def.outputs,
                    'outputs.json',
                ),
            ),
            # 'example result': _detect(
            #     lambda: _read_json_tf_example_from(
            #         signature_def.outputs,
            #         'outputs.pb.json',
            #     ),
            #     lambda: _read_json_example_from(
            #         signature_def.outputs,
            #         'outputs.json',
            #     ),
            # ),
        )

    def _detect(self, *fns):
        for fn in fns:
            result = fn()
            if result is not None:
                return result

    def _feature_for_tensor_info(self, tensor_info):
        tensor_shape = tensor_info.tensor_shape.dim[1:]
        dtype = tf.DType(tensor_info.dtype)
        if tensor_shape[-1].size != -1:
            return tf.FixedLenFeature(dtype=dtype, shape=[dim.size for dim in tensor_shape])
        return tf.VarLenFeature(dtype=dtype)

    def _read_json_tf_example_from(self, tensor_infos, subpath):
        path = self._layout.file(subpath)
        if not os.path.exists(path):
            return None

        with open(path) as f:
            example_dict = tfi.json.load(f)

        with tf.Session(graph=tf.Graph()) as session:
            example_features = {
                name: self._feature_for_tensor_info(tensor_info)
                for name, tensor_info in tensor_infos.items()
            }
            return session.run(
                tf.parse_single_example(
                    ParseDict(example_dict, tf.train.Example()).SerializeToString(),
                    features=example_features))

    def _read_json_example_from(self, tensor_infos, subpath):
        path = self._layout.file(subpath)
        if not os.path.exists(path):
            return None

        with open(path) as f:
            return tfi.data.json(
                f.read(),
                assets_extra_root=self._layout.assets_extra_path)

class MethodDocumentationCodec(object):
    def __init__(self, method_name, method_layout):
        self._name = method_name
        self._layout = method_layout

    def write(self, method_doc):
        metadata = {
            'documentation': {
                'inputs': {
                    name: doc
                    for name, tensor_info, doc in method_doc.inputs()
                },
                'outputs': {
                    name: doc
                    for name, tensor_info, doc in method_doc.outputs()
                },
            },
        }
        MethodDataDocumentationCodec(self._layout).write(method_doc.examples()[0]),
        _write_json(self._layout.metadata_path, metadata)

    def read(self, signature_def):
        metadata = _read_json_else(self._layout.metadata_path, {})
        doc = metadata.get('documentation', {})
        doc_inputs = doc.get('inputs', {})
        doc_outputs = doc.get('outputs', {})

        return tfi.doc.MethodDocumentation(
            name=self._name,
            overview=metadata.get('overview', None),
            inputs=[
                (name, self._tensor_info_str(ti), doc_inputs.get(name, ''))
                for name, ti in signature_def.inputs.items()
            ],
            outputs=[
                (name, self._tensor_info_str(ti), doc_outputs.get(name, ''))
                for name, ti in signature_def.outputs.items()                    
            ],
            examples=[
              MethodDataDocumentationCodec(self._layout).read(signature_def),
            ],
        )

    def _tensor_info_str(self, tensor_info):
        if tensor_info.tensor_shape.unknown_rank:
            return '%s ?' % tf.as_dtype(tensor_info.dtype).name

        return '%s <%s>' % (
            tf.as_dtype(tensor_info.dtype).name,
            ', '.join([
                '?' if dim.size == -1 else str(dim.size)
                for dim in tensor_info.tensor_shape.dim
            ]),
        )

class ModelDocumentationCodec(object):
    def __init__(self, path):
        self._layout = ModelDocumentationLayout(path)

    def _method_codecs(self, method_names):
        return [
            (
                method_name,
                MethodDocumentationCodec(
                    method_name,
                    self._layout.method(method_name),
                )
            )
            for method_name in method_names
        ]

    def write(self, model_doc):
        metadata = {
            'name': model_doc.name(),
            'overview': model_doc.overview(),
            'hyperparameters': [
                (name, str(val_type), val, docs)
                for name, val_type, val, docs in model_doc.hyperparameters()
            ],
            'authors': model_doc.authors(),
            'references': model_doc.references(),
            'implementation_notes': model_doc.implementation_notes(),
            'source': model_doc.source(),
            'facets_overview_proto': None, # model_doc.facets_overview_proto(),
        }

        methods = model_doc.methods()
        for method_name, method_codec in self._method_codecs(methods.keys()):
            method_codec.write(methods[method_name])

        _write_json(self._layout.metadata_path, metadata)

    def read(self, signature_defs):
        metadata = _read_json_else(self._layout.metadata_path, {})
        return tfi.doc.ModelDocumentation(
            # TODO(adamb) Should be transformed to the below structure, with val_type_str -> val_type
            # (name, val_type, val, docs)
            hyperparameters=metadata.get('hyperparameters', []),
            name=metadata.get('name', self._layout.basename),
            overview=metadata.get('overview', None),
            methods=[
                method_codec.read(signature_defs[method_name])
                for method_name, method_codec in self._method_codecs(signature_defs.keys())
            ],
            authors=metadata.get('authors', []),
            references=metadata.get('references', {}),
            implementation_notes=metadata.get('implementation_notes', []),
            source=metadata.get('source', []),
            facets_overview_proto=None,
        )

def read(path, signature_defs):
    return ModelDocumentationCodec(path).read(signature_defs)

def write(path, model_doc):
    return ModelDocumentationCodec(path).write(model_doc)

#!/usr/bin/python
import sys
import re
import string

variables = {}
outs = {}
monomial = "([a-zA-z]+\d+)"
mn = re.compile(monomial)

def extractValues(strin):
        xAddr1 = strin[2].strip()
        xAddr2 = strin[4].strip()
        if xAddr1 in variables:
            x1 = variables[xAddr1]
        else:
            raise Exception("equationReader: variable " + xAddr1 + " not found")
        if mn.match(xAddr2):
            if xAddr2 in variables:
                x2 = variables[xAddr2]
            else:
                raise Exception("equationReader: variable " + xAddr2 + " not found")
        else:
            x2 = bool(int(xAddr2))
        return {'x1':x1, 'x2':x2}

if len(sys.argv) != 3:
    raise Exception("Usage: equationReader <input file> <output file>")

fin = open(sys.argv[1], "r")


lines = fin.readlines()
inputs = re.compile("\d+ inputs")
outputs = re.compile("\d+ outputs")
for index, line in enumerate(lines):
    if inputs.match(line):
        #Creation of the x set
        xLine = lines[index + 1]
        xValueLine = lines[index + 2]
        ins = string.split(xLine)
        insValues = string.split(xValueLine)
        if len(ins) != len(insValues):
            print(line + xLine + xValueLine)
            raise Exception("equationReader: you need to provide a starting value for each x inserted")
        for i in range(len(ins)):
            x = ins[i].strip()
            variables[x] = bool(int(insValues[i]))
    else:
        if outputs.match(line):
            #Creation of the y set
            yLine = lines[index + 1]
            ins = string.split(yLine, " ")
            for y in ins:
                y.strip()
                outs[y] = None
            else:
                if line == "begin":
                    #When the equations start we get to the next cicle which performs the calculations
                    break

#y = x + z
equation_XOR = re.compile(monomial + " = " + monomial + " \+ (" + monomial + "|(0|1))")
#y = x * z
equation_AND = re.compile(monomial + " = " + monomial + " \* (" + monomial + "|(0|1))")
#y = x
equation_ASSIGNEMENT = re.compile(monomial + " = (" + monomial + "|(0|1))")

for index, line in enumerate(lines):
    tmp = string.split(line, " ")
    print(line)
    if equation_XOR.match(line):
        xdict = extractValues(tmp)
        yAddr = tmp[0]
        y = xdict['x1'] ^ xdict['x2']
        variables[yAddr] = y
        if yAddr in outs:
            outs[yAddr] = y
    else:
        if equation_AND.match(line):
            xdict = extractValues(tmp)
            yAddr = tmp[0]
            y = xdict['x1'] & xdict['x2']
            variables[yAddr] = y
            if yAddr in outs:
                outs[yAddr] = y
        else:
            if equation_ASSIGNEMENT.match(line):
                yAddr = tmp[0].strip()
                xAddr = tmp[2].strip()
                if mn.match(xAddr):
                    if xAddr in variables:
                        x = variables[xAddr]
                    else:
                        raise Exception("equationReader: variable " + xAddr + " not found")
                else:
                    x = bool(xAddr)
                y = x
                variables[yAddr] = y
                if yAddr in outs:
                    outs[yAddr] = y
            else:
                print("Skipping malformed equation:" + line)

#Printing out the results
fin.close()
fout = open(sys.argv[2], "w")
for key, value in outs.items():
    fout.write(key + " = {}\n".format(int(value)))
fout.close()

