from django.db import models
from django.db.models.aggregates import Count
from django.utils.translation import gettext_lazy as _

from ...core.models import Project

from .computer import Computer
from .event import Event


class DomainErrorManager(models.Manager):
    def get_queryset(self):
        return super().get_queryset().select_related(
            'project',
            'computer',
            'computer__project',
            'computer__sync_user',
        )

    def scope(self, user):
        qs = self.get_queryset()
        if not user.is_view_all():
            qs = qs.filter(
                project_id__in=user.get_projects(),
                computer_id__in=user.get_computers()
            )

        return qs


class UncheckedManager(DomainErrorManager):
    def get_queryset(self):
        return super().get_queryset().filter(checked=0)

    def scope(self, user):
        return super().scope(user).filter(checked=0)


class ErrorManager(DomainErrorManager):
    def create(self, computer, project, description):
        obj = Error()
        obj.computer = computer
        obj.project = project
        obj.description = description
        obj.save()

        return obj


class Error(Event):
    description = models.TextField(
        verbose_name=_("description"),
        null=True,
        blank=True
    )

    checked = models.BooleanField(
        verbose_name=_("checked"),
        default=False,
    )

    project = models.ForeignKey(
        Project,
        on_delete=models.CASCADE,
        verbose_name=_("project")
    )

    objects = ErrorManager()
    unchecked = UncheckedManager()

    @staticmethod
    def unchecked_count(user=None):
        if not user:
            return Error.unchecked.count()

        return Error.unchecked.scope(user).count()

    @staticmethod
    def unchecked_by_project(user):
        total = Error.unchecked_count(user)

        projects = list(Error.unchecked.scope(user).values(
            'project__name',
            'project__id',
            'project__platform__id',
        ).annotate(
            count=Count('id')
        ).order_by('project__platform__id', '-count'))

        platforms = list(Error.unchecked.scope(user).values(
            'project__platform__id',
            'project__platform__name'
        ).annotate(
            count=Count('id')
        ).order_by('project__platform__id', '-count'))

        return {
            'total': total,
            'inner': platforms,
            'outer': projects,
        }

    @staticmethod
    def status_by_project(user):
        total = Error.objects.scope(user).count()

        projects = list(Error.objects.scope(user).values(
            'computer__status',
            'project__id',
            'project__name',
        ).annotate(
            count=Count('id')
        ).order_by('computer__status', '-count'))

        status = list(Error.objects.scope(user).values(
            'computer__status',
        ).annotate(
            count=Count('id')
        ).order_by('computer__status', '-count'))

        for item in status:
            item['status'] = item.get('computer__status')
            item['computer__status'] = _(dict(Computer.STATUS_CHOICES)[item.get('computer__status')])

        return {
            'total': total,
            'inner': status,
            'outer': projects,
        }

    def checked_ok(self):
        self.checked = True
        self.save()

    def save(self, force_insert=False, force_update=False, using=None, update_fields=None):
        self.description = self.description.replace("\r\n", "\n")

        super().save(force_insert, force_update, using, update_fields)

    class Meta:
        app_label = 'client'
        verbose_name = _('Error')
        verbose_name_plural = _('Errors')

# -*- coding: utf-8 -*-

import re
from module.plugins.Hoster import Hoster


class YoupornCom(Hoster):
    __name__ = "YoupornCom"
    __type__ = "hoster"
    __pattern__ = r'http://(?:www\.)?youporn\.com/watch/.+'
    __version__ = "0.2"
    __description__ = """Youporn.com hoster plugin"""
    __author_name__ = "willnix"
    __author_mail__ = "willnix@pyload.org"

    def process(self, pyfile):
        self.pyfile = pyfile

        if not self.file_exists():
            self.offline()

        pyfile.name = self.get_file_name()
        self.download(self.get_file_url())

    def download_html(self):
        url = self.pyfile.url
        self.html = self.load(url, post={"user_choice": "Enter"}, cookies=False)

    def get_file_url(self):
        """ returns the absolute downloadable filepath
        """
        if not self.html:
            self.download_html()

        return re.search(r'(http://download\.youporn\.com/download/\d+\?save=1)">', self.html).group(1)

    def get_file_name(self):
        if not self.html:
            self.download_html()

        file_name_pattern = r"<title>(.*) - Free Porn Videos - YouPorn</title>"
        return re.search(file_name_pattern, self.html).group(1).replace("&amp;", "&").replace("/", "") + '.flv'

    def file_exists(self):
        """ returns True or False
        """
        if not self.html:
            self.download_html()
        if re.search(r"(.*invalid video_id.*)", self.html) is not None:
            return False
        else:
            return True

