def write_tvshow_nfo(parser, tvshow_api, tvshow_path):
	try:
		if write_tvshow_nfo.favorites:
			parser.Dict().get('tag', []).append('favorites')
	except:
		pass

	NFOWriter(parser, tvshow_api=tvshow_api).write_tvshow_nfo(tvshow_path)
	return

